import React, {useState} from 'react'
import { Card, CardGroup, CardDeck, Button } from 'react-bootstrap';
import heartred from "./heartred.svg";
import heartgray from "./heartgray.svg";


function CardList({title, date, url, explanation}) 
{
  const [liked, setLiked] = useState(false)
  return (
    <div className="container">
    <div className="card">
    <div className="topContainer">
    <div className="title">{title}</div> 

    <div className="date">{date}</div>
    </div>
      <img src={url} style={{width: "100%"}} />
      <div className="bottomContainer">
      {explanation}
      </div>
      <div className="button">
      Like the 
      <img
          src={liked ? heartred : heartgray}
          alt="like button"
          className="like"
          onClick={() => setLiked((curr) => !curr)}
        ></img>
      </div>
    </div> 
    {/* <button onClick={() => imageData()}>Refresh page</button> */}

    </div>
  )
}

export default CardList;
