import {GET_IMAGES} from '../types'

const initialState = {
    images:[],
    loading:true
}

export default function(state = initialState, action){

    switch(action.type){

        case GET_IMAGES:
        return {
            ...state,
            pho:action.payload,
            loading:false

        }
        default: return state
    }

}
