import React, {useState} from "react";

function HomePage() {
  const getImages = async () => {
    const res = await fetch('https://api.nasa.gov/planetary/apod?api_key=rpf2PXcZhgOIA7Tjek38tFq5HDa7a5dsUgDiuVo1&count=5');
    const data = await res.json();
    return data;
 }

 const [photos, setPhotos] = useState([]);
 const [loading, setLoading] = useState(true);
 const handleImages = () => {
   setLoading(true);
     getImages().then((photos) => {
         setPhotos((curr) => [...curr, ...photos]);
         setLoading(false);
     });
 };
 useEffect(() => {
   handleImages();
}, []);

  return (

<>
  <div className="container">
<h2>Spaceagram</h2>
        <div className="cardFlex">

            {photos.length > 0 &&
            photos.map(
              (photo) =>
                photo.media_type === "image" && (
                  <CardList
                    title={photo.title}
                    date={photo.date}
                    url={photo.url}
                    key={photo.title}
                    explanation={photo.explanation}
                  />
                )
            )}
            {loading && <div className="load">Loading...</div>}

</div>
           <div className="sync"> <img src={sync} alt="load more" onClick={handleImages}></img> </div>
            </div>
            </>

  );
}

export default HomePage;
